mapper.update:

update fui_dict_entry
set dict_code = #{dictCode,jdbcType=VARCHAR},
  dict_entry_code = #{dictEntryCode,jdbcType=VARCHAR},
  dict_entry_desc = #{dictEntryDesc,jdbcType=VARCHAR},
  dict_entry_sort = #{dictEntrySort,jdbcType=BIGINT}
where id = #{id,jdbcType=INTEGER}

mapper.select:
SELECT
    id	as "id",
    dict_code	as "dictCode",
    dict_entry_code	as "dictEntryCode",
    dict_entry_desc	as "dictEntryDesc",
    dict_entry_sort	as "dictEntrySort"
FROM fui_dict_entry WHERE 1=1

    AND id = #{id}


    AND dict_code = #{dictCode}


    AND dict_entry_code IN ${dictEntryCodeArgs}


    ORDER BY ${orderBy}
